func (bus *BackendUserSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, bus.ctx, ent.OpQuerySelect)
	if err := bus.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*BackendUserQuery, *BackendUserSelect](ctx, bus.BackendUserQuery, bus, bus.inters, v)
}

func (bus *BackendUserSelect) sqlScan(ctx context.Context, root *BackendUserQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(bus.fns))
	for _, fn := range bus.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*bus.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := bus.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

// Modify adds a query modifier for attaching custom logic to queries.
func (bus *BackendUserSelect) Modify(modifiers ...func(s *sql.Selector)) *BackendUserSelect {
	bus.modifiers = append(bus.modifiers, modifiers...)
	return bus
}
