// UpdateNewValues updates the mutable fields using the new values that
// were set on create. Using this option is equivalent to using:
//
//	client.Department.Create().
//		OnConflict(
//			sql.ResolveWithNewValues(),
//			sql.ResolveWith(func(u *sql.UpdateSet) {
//				u.SetIgnore(department.FieldID)
//			}),
//		).
//		Exec(ctx)
func (u *DepartmentUpsertBulk) UpdateNewValues() *DepartmentUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWithNewValues())
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(s *sql.UpdateSet) {
		for _, b := range u.create.builders {
			if _, exists := b.mutation.ID(); exists {
				s.SetIgnore(department.FieldID)
			}
			if _, exists := b.mutation.CreatedAt(); exists {
				s.SetIgnore(department.FieldCreatedAt)
			}
			if _, exists := b.mutation.Code(); exists {
				s.SetIgnore(department.FieldCode)
			}
			if _, exists := b.mutation.DepartmentType(); exists {
				s.SetIgnore(department.FieldDepartmentType)
			}
			if _, exists := b.mutation.MerchantID(); exists {
				s.SetIgnore(department.FieldMerchantID)
			}
			if _, exists := b.mutation.StoreID(); exists {
				s.SetIgnore(department.FieldStoreID)
			}
		}
	}))
	return u
}

// Ignore sets each column to itself in case of conflict.
