// Scan applies the selector query and scans the result into the given value.
func (tfs *TaxFeeSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, tfs.ctx, ent.OpQuerySelect)
	if err := tfs.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*TaxFeeQuery, *TaxFeeSelect](ctx, tfs.TaxFeeQuery, tfs, tfs.inters, v)
}

func (tfs *TaxFeeSelect) sqlScan(ctx context.Context, root *TaxFeeQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(tfs.fns))
	for _, fn := range tfs.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*tfs.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := tfs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

// Modify adds a query modifier for attaching custom logic to queries.
func (tfs *TaxFeeSelect) Modify(modifiers ...func(s *sql.Selector)) *TaxFeeSelect {
	tfs.modifiers = append(tfs.modifiers, modifiers...)
	return tfs
}
